#!/usr/bin/ruby

input = (ARGV.empty? ? DATA : ARGF).each_line.map(&:chomp).freeze

#input.each {|l|
#	print(l, "\n")
#}

def find_next_pos(pipeline, pos, v)
	next_pos = [ pos[0] + v[0], pos[1] + v[1] ]
	case pipeline[ pos[1] ][ pos[0] ]
	when '-', '|', 'A'..'Z'
		next_pos = nil if pipeline[ next_pos[1] ][ next_pos[0] ] == ' '
	when '+'
		if v[0] == 0	# vertical movement
			if pipeline[ pos[1] ][ pos[0]+1 ] =~ /[A-Z-]/
				next_pos = [ pos[0]+1, pos[1] ]
			else
				next_pos = [ pos[0]-1, pos[1] ]
			end
		else
			if pipeline[ pos[1]+1 ][ pos[0] ] =~ /[A-Z|]/
				next_pos = [ pos[0], pos[1]+1 ]
			else
				next_pos = [ pos[0], pos[1]-1 ]
			end
		end
	end
	return next_pos
end
		
def walk_pipeline(pipeline)
	start_x = (pipeline[0] =~ / \| /) + 1
	start_y = 0
	pos = [start_x, start_y]
	v = [0, 1]
	out_str = ''
	move_cnt = 1
	loop do 
#		print pos, pipeline[pos[1]] [pos[0]]
		out_str << pipeline[ pos[1] ][ pos[0] ] if ('A'..'Z').include?(pipeline[ pos[1] ][ pos[0] ])
		next_pos = find_next_pos(pipeline, pos, v)
#		print " -> #{next_pos}\n"
		break if next_pos == nil
		v = [ next_pos[0] - pos[0], next_pos[1] - pos[1] ]
		pos = next_pos
		move_cnt += 1
	end
	return [out_str, move_cnt]
end

print ("P1-P2 chars on pipeline, number of moves: #{walk_pipeline(input)}\n")

